element.calculation:
var minutes = current.time_total.getDisplayValue();
	
function convertMinutesToHours (data) {
	var hours = Math.floor(data / 60);
	var minutes = data % 60;
	if (minutes < 10){ minutes = '0' + minutes; }
	var timeString = hours + ':' + minutes + ':00';
	return timeString;
}
convertMinutesToHours(minutes);
